import React, { Component } from 'react';

class AgeLabel extends Component {
    render() {



        return (
            <div >
                <div className="form-group">
                <select id="iller" className="form-control" >
                    <option> Age</option>
                    <option>13-18</option> 
                    <option>25+</option> 
                </select>
                <input class="form-check-input" type="checkbox" id="gridCheck" />
                <label class="form-check-label" for="gridCheck">
                   Kids accompanying?
        </label>

        {/* On click, it will display the following options: infants, toddlers, preschooler, school-aged kids and adolescent*/}
                </div>
            </div>
        );
    }
}

export default AgeLabel;
