import React, { Component } from 'react';
import Square from './Square.js';
import AgeLabel from './AgeLabel';
import AgeKids from './AgeKids.js';
import MealPreferences from './MealPreferences.js';
import Interests from './Interests';
import IncrementComponent from './IncrementComponent.js';

class Card extends Component {

    state={
        Adults:0,
        Seniors:0,
        Youth:0,
        Child:0,
        Infant:0,
        andthenjesaiplus:0,

    }
    IncrementItem = (fieldName) => {
        this.setState({ clicks: this.state.clicks + 1 });
      }
      DecreaseItem = (fieldName) => {
        this.setState({ clicks: this.state.clicks - 1 });
      }
      ToggleClick = () => {
        this.setState({ show: !this.state.show });
      }

    render() {
        var styles = {
            cardStyle: {
                height: 400,
                width: 300,
                float: "left",
                backgroudColor: "#FFF",
                boxShadow: "0px 0px 5px #666",
                margin: "20px",
            },

        }

        return (
            <div>
                <h1> Profile </h1>

                <div style={styles.cardContainer}>
                    <div style={styles.cardStyle}>
                        <Square />

                        <div style={styles.titleStyle}>


                            <table className="table">

                                <tr>
                                    <td>Adults  (18-64)</td>
                                    <td> <IncrementComponent name="adult" increment={this.increment} decrement={this.decriment} /></td>
                                </tr>
                                <tr>
                                    <td>Seniors (65+)</td>
                                    <td><IncrementComponent name="senior" increment={this.increment} decrement={this.decriment}/></td>

                                </tr>
                                <tr>
                                    <td>Youth (12-17)</td>
                                    <td><IncrementComponent increment={this.increment} decrement={this.decriment}/></td>

                                </tr>
                                <tr>
                                    <td>Child (2-11)</td>
                                    <td><IncrementComponent increment={this.increment} decrement={this.decriment}/></td>

                                </tr>
                                <tr>
                                    <td>Seat Infant (under 2)</td>
                                    <td><IncrementComponent increment={this.increment} decrement={this.decriment}/></td>

                                </tr>
                                <tr>
                                    <td>Lap Infant (under 2)</td>
                                    <td><IncrementComponent increment={this.increment} decrement={this.decriment}/></td>

                                </tr>
                            </table>



                        </div>


                    </div>



                    <div style={styles.cardStyle}>
                        <Square />
                        <MealPreferences />


                    </div>

                    <div style={styles.cardStyle}>
                        <Square />
                        <p> </p>
                        <Interests />

                    </div>
                </div>

            </div>
        );
    }
}

export default Card;
