import React, { Component } from 'react';
import { Map, GoogleApiWrapper, InfoWindow, Marker, google  } from 'google-maps-react';
import {connect} from 'react-redux'

const mapStyles = {
  width: '50%',
  height: '60%'
};

 class MapContainer extends Component {
    state = {
        showingInfoWindow: false,  //Hides or the shows the infoWindow
        activeMarker: {},          //Shows the active marker upon click
        selectedPlace: {}          //Shows the infoWindow to the selected place upon a marker
      };
      onMarkerClick = (props, marker, e) =>
    this.setState({
      selectedPlace: props,
      activeMarker: marker,
      showingInfoWindow: true
    });

  onClose = props => {
    if (this.state.showingInfoWindow) {
      this.setState({
        showingInfoWindow: false,
        activeMarker: null
      });
    }
  };

  componentWillMount(){
    const locations = this.props.markerLocations;
    const geocoder = new window.google.maps.Geocoder();
    geocoder.geocode({addresses:locations.from}, (result, status)=>{
      if(status=='OK'){
        const geometry = result[0].geometry.location
        const coordinates = {
          lat: geometry.lat(),
          lng:geometry.lng()
        }
        console.log("THE COORDINATES FOR FROM ", coordinates)
      }
    })  
  }


    render() {
      return (
        <Map
          google={this.props.google}
          zoom={14}
          style={mapStyles}
          initialCenter={{
           lat: -1.2884,
           lng: 36.8233
          }}
        >
        <Marker
          onClick={this.onMarkerClick}
          name={'Kenyatta International Convention Centre'}
        />
        <InfoWindow
          marker={this.state.activeMarker}
          visible={this.state.showingInfoWindow}
          onClose={this.onClose}
        >
          <div>
            <h4>{this.state.selectedPlace.name}</h4>
          </div>
        </InfoWindow>
      </Map>


      );
    }

  }

  function mapStateToProps(state){

    return {
      markerLocations: state.mapReducer.data
    }
  }

  export const mapped = connect(mapStateToProps)(MapContainer)

  export default GoogleApiWrapper({
    apiKey: 'AIzaSyC64eUv-Iw47Q9MqhsnpgzpvCUm-SM1qi4'
  })(mapped);
