import React, { Component } from 'react';
import { Map, GoogleApiWrapper, InfoWindow, Marker, google  } from 'google-maps-react';
import {connect} from 'react-redux'

const mapStyles = {
  width: '50%',
  height: '60%'
};

 class MapContainer extends Component {
    state = {
        showingInfoWindow: false,  //Hides or the shows the infoWindow
        activeMarker: {},          //Shows the active marker upon click
        selectedPlace: {} ,         //Shows the infoWindow to the selected place upon a marker

      };
      onMarkerClick = (props, marker, e) =>
    this.setState({
      selectedPlace: props,
      activeMarker: marker,
      showingInfoWindow: true
    });

  onClose = props => {
    if (this.state.showingInfoWindow) {
      this.setState({
        showingInfoWindow: false,
        activeMarker: null
      });
    }
  };





    render() {
     this.renderMarkers();

        return (
          <Map
            google={this.props.google}
            zoom={14}
            style={mapStyles}
            initialCenter={{
             lat: -1.2884,
             lng: 36.8233
            }}
          >
          <Marker
            onClick={this.onMarkerClick}
            name={'Kenyatta International Convention Centre'}
          />
          <InfoWindow
            marker={this.state.activeMarker}
            visible={this.state.showingInfoWindow}
            onClose={this.onClose}
          >
            <div>
              <h4>{this.state.selectedPlace.name}</h4>
            </div>
          </InfoWindow>
        </Map>


        )
      }


  }

  function Geocode(wrappedComponent){

    return class extends React.Component{

      constructor(){
        super()
        this.state= {markers:[]}
      }

      componentWillMount(){
        if(this.props.markerLocations ){
          const locations = this.props.markerLocations;

          this.getLocationCoordinates(locations.from);
          this.getLocationCoordinates(locations.to);
           console.log("THE LOC ",this.state.markers)
        }

      }

      getLocationCoordinates(location){
        const geocoder = new window.google.maps.Geocoder();
        geocoder.geocode({address:location}, (result, status)=>{
          if(status=='OK'){
            const geometry = result[0].geometry.location
            const coordinates = {
              lat: geometry.lat(),
              lng:geometry.lng()
            }
           this.setState(prev=>{
             prev.markers.push(coordinates)
           })
          }
        }) 
      }


   renderMarkers(){
    console.log(" THE STATE ",this.state.markers);
     this.state.markers.map(location =>{
       console.log(" A LOCATION ", location)
     })
   }

   render(){
     return (<wrappedComponent {...this.props}/>);
   }

    }

  }

  function mapStateToProps(state){

    return {
      markerLocations: state.mapReducer.data
    }
  }

  export const mapped = connect(mapStateToProps)(Geocode(MapContainer))

  export default GoogleApiWrapper({
    apiKey: 'AIzaSyC64eUv-Iw47Q9MqhsnpgzpvCUm-SM1qi4'
  })(mapped);
