import  {createStore, applyMiddleware, combineReducers, compose} from 'redux';
import {mapReducer} from './map-reducer';




export const init = () => {

    const reducer = redux.combineReducers({mapReducer});

    const store = redux.createStore(reducer);

    return store;
}
